 function dimerCheck(){
	 
		if(checkForPrimerPair()==true){
		selectPrimers();
        selectedFPrimer 
        
        selectedRPrimer=selectedRPrimer.split("").reverse().join("");
        selectedRPrimer = complement2(selectedRPrimer);
        var rshort = selectedRPrimer.substring(0, 3);
        var fshort = selectedFPrimer.substring(selectedFPrimer.length - 3);
        
        if(rshort == fshort){
			alert("Primer dimer is possible (complementary 3'ends) and redesign is recommended.")
		}else{
			alert('Primer dimer not found.')
		}
        
        }else{alert('Select 1 forward and 1 reverse primer only.');}
    }
    
 function complement2 (seq)	{
	seq = seq.replace(/g/g,"x");
	seq = seq.replace(/G/g,"X");
	seq = seq.replace(/a/g,"y");
	seq = seq.replace(/A/g,"Y");
	seq = seq.replace(/t/g,"a");
	seq = seq.replace(/T/g,"A");
	seq = seq.replace(/c/g,"g");
	seq = seq.replace(/C/g,"G");
	seq = seq.replace(/x/g,"c");
	seq = seq.replace(/X/g,"C");
	seq = seq.replace(/y/g,"t");
	seq = seq.replace(/Y/g,"T");		
	return seq;
}
    
